# ---------- Compter le nombre de handle

def handle_count (arr_handle)
number = arr_handle.length
puts "Nombre de journalistes: #{number}"
return number
end

# ---------- Handle le plus court
def shortest_handle (arr_handle)
shortest = arr_handle.sort!{|a, b| a.length <=> b.length} [0]
puts "Le handle le plus court est: #{shortest}"
return shortest
end

# ---------- Combien y-a-t'il de handle contenant 5 caractères ?
def five_handle (arr_handle)
five = arr_handle.count{|i| i.length==6}
puts "Nombre de handles avec 5 caractères : #{five}"
return five
end

# ---------- Combien commencent par une majuscule ?
def uppercase (arr_handle)
uppercase_count =  arr_handle.count{|i| i[1,1] =~ /[A-Z]/}
puts "Nombre de majuscules: #{uppercase_count} "
end

# ----------- Trie la liste de handle par ordre alphabétique.
def alphabetical (arr_handle)
order = arr_handle.sort_by {|arr_handle|arr_handle}
puts "Voici la liste des handle par ordre alphabétique: "
puts order
return order
end

# ----------- Trie la liste de handle par taille des handle
def size_handle (arr_handle)
size = arr_handle.sort_by(&:length)
puts "Voici les handle du plus petit au plus grand: "
puts size
return size
end

# ---------- Quelle est la position dans l'array de la personne @epenser ?

def position_epenser (arr_handle)
position = arr_handle.find_index("@epenser")
puts "Voici la position où se trouve @epenser: #{position}"
return position
end

# ---------- Sors-moi une répartition des handle par taille de ces derniers

def size_division (arr_handle)
 x = 1
  while x < 34
  division = arr_handle.count{|i| i.length==x}
  x = x + 1
  if division > 0
  then puts " Il y a #{division} handles avec #{x-1} caractères"
  end
end
return division
end


# ---------- Perform
def perform 
	arr_handle = journalists
	number = handle_count (arr_handle)
	shortest = shortest_handle (arr_handle)
	five = five_handle (arr_handle)
	uppercase_count = uppercase (arr_handle)
	order = alphabetical (arr_handle)
	size = size_handle (arr_handle)
	position = position_epenser (arr_handle)
	division = size_division (arr_handle)
   
end

